import React, { useState, useEffect, useCallback } from "react";

const NewsFeed = () => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [page, setPage] = useState(1);
  const pageSize = 10; // Fetch 10 articles at a time

  const fetchNews = useCallback(async () => {
    setLoading(true);
    setError(null);

    try {
      const apiKey = "6dc86d2008a94186a558eba6989d2ea2"; // Replace with your valid API key
      const apiUrl = `https://newsapi.org/v2/top-headlines?country=us&pageSize=${pageSize}&page=${page}&apiKey=${apiKey}`;

      const response = await fetch(apiUrl);
      const data = await response.json();

      if (data.status === "ok") {
        setNews(prevNews => [...prevNews, ...data.articles]); // Append new articles
      } else {
        throw new Error(data.message || "Failed to fetch news");
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  }, [page]);

  useEffect(() => {
    fetchNews();
  }, [fetchNews]);

  return (
    <div style={{ padding: "20px" }}>
      <h2 style={{ 
        color: "#00ff88", // Bright neon green for main heading
        fontSize: "28px",
        marginBottom: "24px",
        borderBottom: "2px solid #00ff88",
        paddingBottom: "8px"
      }}>Latest News</h2>

      {loading && <p style={{ color: "#00ff88" }}>Loading news...</p>}
      {error && <p style={{ color: "#ff4444" }}>Failed to fetch news. Try again.</p>}

      {!loading && !error && (
        <ul style={{ listStyleType: "none", padding: 0 }}>
          {news.map((article, index) => (
            <li key={index} style={{ 
              marginBottom: "20px", 
              backgroundColor: "#1a1a1a", // Slightly lighter than pure black
              padding: "16px", 
              borderRadius: "12px",
              boxShadow: "0 4px 6px rgba(0, 255, 136, 0.1)", // Subtle neon glow
              transition: "transform 0.2s ease",
              cursor: "pointer",
              "&:hover": {
                transform: "translateY(-2px)"
              }
            }}>
              <h3 style={{ 
                color: "#ffffff", // Pure white for article titles
                fontSize: "18px",
                marginBottom: "8px"
              }}>{article.title}</h3>
              <p style={{ 
                color: "#b3b3b3", // Light grey for description
                fontSize: "14px",
                lineHeight: "1.5"
              }}>{article.description}</p>
              <a href={article.url} 
                target="_blank" 
                rel="noopener noreferrer" 
                style={{ 
                  color: "#00ff88", // Matching neon green
                  textDecoration: "none",
                  fontWeight: "bold",
                  "&:hover": {
                    textDecoration: "underline"
                  }
                }}>
                Read More â†’
              </a>
            </li>
          ))}
        </ul>
      )}

      <button
        onClick={() => setPage(page + 1)}
        style={{ 
          backgroundColor: "#00ff88",
          color: "#000000", // Black text on neon button
          padding: "12px 24px",
          borderRadius: "8px",
          cursor: "pointer",
          border: "none",
          fontWeight: "bold",
          transition: "all 0.2s ease",
          "&:hover": {
            backgroundColor: "#00cc6f",
            transform: "translateY(-2px)"
          }
        }}
      >
        Load More
      </button>
    </div>
  );
};

export default NewsFeed;
