import React, { useState, useEffect } from "react";
import Navbar from "./Navbar";
import NewsFeed from "./NewsFeed";

function App() {
  const [searchQuery, setSearchQuery] = useState("");
  const [weather, setWeather] = useState(null);
  const [state, setState] = useState("");

  const handleSearch = (query) => {
    console.log("Search query received in App:", query); // Debug log
    setSearchQuery(query);
  };

  useEffect(() => {
    const latitude = 10.8505; // Kochi coordinates
    const longitude = 76.2711;
    
    const fetchWeather = async () => {
      try {
        const weatherRes = await fetch(
          `https://api.open-meteo.com/v1/forecast?latitude=${latitude}&longitude=${longitude}&current_weather=true`
        );
        const weatherData = await weatherRes.json();
        console.log(weatherData); // Log the response for debugging
    
        if (weatherData.current_weather) {
          setWeather(weatherData.current_weather);
          setState("KOCHI");
        } else {
          console.error("Current weather data is not available.");
        }
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchWeather();
  }, []);

  return (
    <div style={styles.page}>
      <Navbar onSearch={handleSearch} />

      <div style={styles.container}>
        <div style={styles.briefing}>
          <h1 style={styles.heading}>Your Daily Briefing</h1>
          <p style={styles.date}>{new Date().toLocaleDateString('en-US', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}</p>
        </div>

        <div style={styles.grid}>
          {/* Main News Column */}
          <div style={styles.centerColumn}>
            <NewsFeed searchQuery={searchQuery} />
          </div>

          {/* Weather Column */}
          <div style={styles.rightColumn}>
            {weather && state && (
              <div style={styles.weatherCard}>
                <h3 style={styles.sectionTitle}>Current Weather</h3>
                <div style={styles.weatherContent}>
                  <div style={styles.weatherHeader}>
                    <span style={styles.cityName}>{state}</span>
                    <span style={styles.weatherIcon}>
                      {weather.weathercode === 0 ? "☀️" : "☁️"}
                    </span>
                  </div>
                  <div style={styles.weatherDetails}>
                    <span style={styles.temperature}>{weather.temperature}°C</span>
                    <span style={styles.climate}>
                      {weather.weathercode === 0 ? "Clear Sky" : "Cloudy"}
                    </span>
                  </div>
                </div>
              </div>
            )}
            
            <div style={styles.quickLinksCard}>
              <h3 style={styles.sectionTitle}>Quick Links</h3>
              <div style={styles.quickLinksGrid}>
                <div style={styles.quickLink}>World</div>
                <div style={styles.quickLink}>Business</div>
                <div style={styles.quickLink}>Tech</div>
                <div style={styles.quickLink}>Health</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

// Enhanced Styles
const styles = {
  page: {
    fontFamily: "'Poppins', 'Segoe UI', Roboto, sans-serif",
    backgroundColor: "#0f172a", // Darker blue for better contrast
    minHeight: "100vh",
    paddingTop: "80px",
    color: "#e2e8f0",
  },
  container: { 
    maxWidth: "1200px", 
    margin: "0 auto", 
    padding: "20px",
    animation: "fadeIn 0.5s ease-in-out",
  },
  briefing: { 
    marginBottom: "30px",
    borderBottom: "1px solid #334155",
    paddingBottom: "15px",
  },
  heading: { 
    color: "#38bdf8", // Lighter blue for headings
    fontWeight: "700",
    fontSize: "32px",
    marginBottom: "8px",
    letterSpacing: "0.5px",
  },
  date: { 
    color: "#94a3b8", 
    fontSize: "16px",
    fontWeight: "400",
  },
  grid: {
    display: "grid",
    gridTemplateColumns: "2fr 1fr",
    gap: "24px",
  },
  centerColumn: {
    display: "flex",
    flexDirection: "column",
    gap: "24px",
  },
  rightColumn: {
    display: "flex",
    flexDirection: "column",
    gap: "24px",
  },
  sectionTitle: {
    color: "#38bdf8",
    fontSize: "18px",
    fontWeight: "600",
    marginBottom: "16px",
    textTransform: "uppercase",
    letterSpacing: "1px",
    borderBottom: "1px solid #334155",
    paddingBottom: "8px",
  },
  weatherCard: {
    backgroundColor: "#1e293b", // Slightly lighter card background
    padding: "24px",
    borderRadius: "16px",
    boxShadow: "0 10px 15px -3px rgba(0, 0, 0, 0.3)",
    transition: "transform 0.2s ease-in-out, box-shadow 0.2s ease-in-out",
    border: "1px solid #334155",
  },
  weatherContent: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    gap: "12px",
  },
  weatherHeader: {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    width: "100%",
  },
  cityName: {
    color: "#e2e8f0",
    fontSize: "22px",
    fontWeight: "600",
  },
  weatherIcon: {
    fontSize: "36px",
  },
  weatherDetails: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    marginTop: "8px",
  },
  temperature: {
    color: "#38bdf8",
    fontWeight: "bold",
    fontSize: "40px",
    textShadow: "0 2px 10px rgba(56, 189, 248, 0.3)",
  },
  climate: {
    color: "#cbd5e1",
    fontSize: "18px",
    marginTop: "4px",
  },
  quickLinksCard: {
    backgroundColor: "#1e293b",
    padding: "24px",
    borderRadius: "16px",
    boxShadow: "0 10px 15px -3px rgba(0, 0, 0, 0.3)",
    border: "1px solid #334155",
  },
  quickLinksGrid: {
    display: "grid",
    gridTemplateColumns: "1fr 1fr",
    gap: "12px",
  },
  quickLink: {
    backgroundColor: "#334155",
    color: "#e2e8f0",
    padding: "12px",
    borderRadius: "8px",
    textAlign: "center",
    fontWeight: "500",
    cursor: "pointer",
    transition: "all 0.2s ease",
    border: "1px solid #475569",
    textShadow: "0 1px 2px rgba(0, 0, 0, 0.3)",
  },
};

export default App;
