import React, { useState, useEffect, useCallback } from "react";

const NewsFeed = ({ searchQuery }) => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [page, setPage] = useState(1);
  const [displayedQuery, setDisplayedQuery] = useState("");
  const pageSize = 10; // Fetch 10 articles at a time

  // Reset news and page when searchQuery changes
  useEffect(() => {
    console.log("Search query changed in NewsFeed:", searchQuery); // Debug log
    setDisplayedQuery(searchQuery);
    setNews([]);
    setPage(1);
    // Fetch news with the search query if it exists
    if (searchQuery && searchQuery.trim() !== "") {
      fetchNewsWithSearch(searchQuery, 1);
    } else {
      fetchNews(1);
    }
  }, [searchQuery]);

  const fetchNews = useCallback(async (pageNum = page) => {
    setLoading(true);
    setError(null);

    try {
      const apiKey = "712fd851ef324f558b6625cb24c7312b"; // Replace with your valid API key
      const apiUrl = `https://newsapi.org/v2/top-headlines?country=us&pageSize=${pageSize}&page=${pageNum}&apiKey=${apiKey}`;

      console.log("Fetching general news from:", apiUrl); // Debug log
      const response = await fetch(apiUrl);
      const data = await response.json();

      if (data.status === "ok") {
        if (pageNum === 1) {
          setNews(data.articles); // Replace news for page 1
        } else {
          setNews(prevNews => [...prevNews, ...data.articles]); // Append for subsequent pages
        }
      } else {
        throw new Error(data.message || "Failed to fetch news");
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  }, [page, pageSize]);

  const fetchNewsWithSearch = useCallback(async (query, pageNum = page) => {
    if (!query || query.trim() === "") return;
    
    setLoading(true);
    setError(null);

    try {
      const apiKey = "712fd851ef324f558b6625cb24c7312b"; // Replace with your valid API key
      const apiUrl = `https://newsapi.org/v2/everything?q=${encodeURIComponent(query)}&pageSize=${pageSize}&page=${pageNum}&apiKey=${apiKey}`;

      console.log("Fetching news with search query:", apiUrl); // Debug log
      const response = await fetch(apiUrl);
      const data = await response.json();

      if (data.status === "ok") {
        if (pageNum === 1) {
          setNews(data.articles); // Replace news for page 1
        } else {
          setNews(prevNews => [...prevNews, ...data.articles]); // Append for subsequent pages
        }
      } else {
        throw new Error(data.message || "Failed to fetch news");
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  }, [page, pageSize]);

  const handleLoadMore = () => {
    const newPage = page + 1;
    setPage(newPage);
    
    if (searchQuery && searchQuery.trim() !== "") {
      fetchNewsWithSearch(searchQuery, newPage);
    } else {
      fetchNews(newPage);
    }
  };

  // Format publish date
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', { 
      month: 'short', 
      day: 'numeric', 
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  // Get category badge background color
  const getCategoryColor = (category) => {
    const categories = {
      "business": "#3b82f6",
      "entertainment": "#ec4899",
      "general": "#6366f1",
      "health": "#10b981",
      "science": "#f59e0b",
      "sports": "#ef4444",
      "technology": "#8b5cf6"
    };
    
    return categories[category?.toLowerCase()] || "#6366f1";
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>
        {displayedQuery ? `Search Results` : `Today's Headlines`}
      </h2>
      
      {displayedQuery && (
        <div style={styles.searchInfoContainer}>
          <p style={styles.searchInfo}>
            <span style={styles.queryText}>"{displayedQuery}"</span>
          </p>
        </div>
      )}

      {loading && (
        <div style={styles.loadingContainer}>
          <div style={styles.loadingSpinner}></div>
          <p style={styles.loadingText}>Fetching the latest stories...</p>
        </div>
      )}
      
      {error && <div style={styles.errorContainer}>
        <p style={styles.errorText}>
          <span style={styles.errorIcon}>‚ö†Ô∏è</span> 
          Failed to fetch news: {error}
        </p>
      </div>}

      {!loading && !error && news.length === 0 && displayedQuery && (
        <div style={styles.noResultsContainer}>
          <p style={styles.noResultsText}>
            <span style={styles.noResultsIcon}>üîç</span>
            No results found for "{displayedQuery}"
          </p>
          <p style={styles.noResultsTip}>Try different keywords or check your spelling</p>
        </div>
      )}

      {!loading && !error && news.length > 0 && (
        <ul style={styles.newsList}>
          {news.map((article, index) => (
            <li key={index} style={styles.newsItem}>
              {article.urlToImage && (
                <div style={styles.imageContainer}>
                  <img 
                    src={article.urlToImage} 
                    alt={article.title} 
                    style={styles.newsImage}
                    onError={(e) => {
                      e.target.style.display = 'none';
                    }}
                  />
                </div>
              )}
              <div style={styles.contentContainer}>
                <div style={styles.newsHeader}>
                  {article.source && article.source.name && (
                    <span style={styles.source}>{article.source.name}</span>
                  )}
                  {article.publishedAt && (
                    <span style={styles.publishDate}>{formatDate(article.publishedAt)}</span>
                  )}
                </div>
                <h3 style={styles.newsTitle}>{article.title}</h3>
                {article.description && (
                  <p style={styles.newsDescription}>{article.description}</p>
                )}
                <div style={styles.newsFooter}>
                  <a 
                    href={article.url} 
                    target="_blank" 
                    rel="noopener noreferrer" 
                    style={styles.newsLink}>
                    Read Full Story
                  </a>
                  
                  {/* Category badge - normally would come from API but using placeholder */}
                  <span 
                    style={{
                      ...styles.categoryBadge,
                      backgroundColor: getCategoryColor(article.source?.category || "general")
                    }}
                  >
                    {article.source?.category || "News"}
                  </span>
                </div>
              </div>
            </li>
          ))}
        </ul>
      )}

      {!loading && !error && news.length > 0 && (
        <button
          onClick={handleLoadMore}
          style={styles.loadMoreButton}
        >
          Load More Stories
        </button>
      )}
    </div>
  );
};

const styles = {
